import { useState } from 'react';
import { StyleSheet, Text, View, Button, TextInput, Pressable } from 'react-native';
import DropdownComponent from '../dropdowncomponents/DropdownComponent';
import DateDropdownComponent from '../dropdowncomponents/DateDropdownComponent';
import TimeDropdownComponent from '../dropdowncomponents/TimeDropdownComponent';

import {styles} from './HomePage.jsx';

export default function QuestionsForProviders ({navigation}) {


    return (

        <View style = {questionsStyles.container}>
            <View style = {questionsStyles.logo}>
                <Text>Logo</Text>
            </View>

            <View style = {questionsStyles.header}>
                <Text>Header</Text>
            </View>

            <View style = {questionsStyles.buttons}>
                <View style = {questionsStyles.location}>
                    <Text>Location</Text>
                </View>

                <View style = {questionsStyles.date}>
                    <Text>Date</Text>
                    <DateDropdownComponent></DateDropdownComponent>
                </View>

                <View style = {questionsStyles.time}>
                    <Text>Time</Text>
                    <TimeDropdownComponent></TimeDropdownComponent>
                </View>

                <View style = {questionsStyles.event}>
                    <Text>Event</Text>
                </View>
            </View>
        </View>
    );
};

const questionsStyles = StyleSheet.create({
    container: {
        flex: 1,
        margin: 20,
        paddingHorizontal: 16,
    },
    input: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems:'center',
        marginBottom: 24, 
        borderBottomWidth: 1,
        borderBottomColor: 'grey'
    },
    textInput: {
        borderWidth: 1,
        borderColor: 'grey',
        width: '70%',
        marginRight: 8,
        padding: 8
    },
    goals: {
        flex: 5
    },
    goalItem: {
        margin: 8,
        borderRadius: 6,
        backgroundColor: '#5e0acc',
        padding: 8,
        color: 'white'
    },



});
