// Users.js

import React, { useState } from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';
import { useNavigation } from '@react-navigation/native'; // Import useNavigation hook
import RightNow from './screens/RightNow';

const SinkingButton = ({ onPress, title, backgroundColor, textColor }) => {
  const [pressed, setPressed] = useState(false);

  const handlePressIn = () => {
    setPressed(true);
  };

  const handlePressOut = () => {
    setPressed(false);
  };

  return (
    <TouchableOpacity
      onPress={onPress}
      onPressIn={handlePressIn}
      onPressOut={handlePressOut}
      style={[styles.button, { backgroundColor }, pressed && styles.buttonPressed]}
    >
      <Text style={[styles.text, { color: textColor }]}>{title}</Text>
    </TouchableOpacity>
  );
};

const Users = () => {
  const navigation = useNavigation(); // Get the navigation object using useNavigation hook

  return (
    <View style={styles.container}>
      <Text style={styles.headerText}>users</Text>
      <View>
        <SinkingButton
          title="right now"
          backgroundColor="#3455A9"
          textColor="#EAF0FF"
          onPress={() => navigation.navigate('RightNow')} // Navigate to the RightNow screen
        />
      </View>
      <View style={styles.rightNow}>
        <SinkingButton
          onPress={handlePress}
          title="future"
          backgroundColor="#CE8239"
          textColor="#2D3548"
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  headerText: {
    fontFamily: 'Montserrat_700Bold',
    fontSize: 64,
    color: '#2D3548',
    marginBottom: 20,
  },
  button: {
    paddingVertical: 12,
    paddingHorizontal: 20,
    borderRadius: 28,
    elevation: 3,
    width: 171.75,
    height: 48,
    justifyContent: 'center',
    alignItems: 'center',
  },
  buttonPressed: {
    transform: [{ scale: 0.95 }],
  },
  text: {
    fontFamily: 'Montserrat_700Bold',
    fontWeight: 'bold',
    fontSize: 24,
  },
  rightNow: {
    marginBottom: 15,
  },
});

export default Users;
