import { useState } from 'react';
import { StyleSheet, Text, View, Button, TextInput, Pressable } from 'react-native';


export default function QuestionsForProviders ({navigation}) {
    return (
        <View style = {styles.container}>
            <View style = {styles.logo}>
                <Text>Logo</Text>
            </View>

            <View style = {styles.header}>
                <Text>Header</Text>
            </View>

            <View style = {styles.buttons}>
                <View style = {styles.location}>
                    <Text>Location</Text>
                </View>

                <View style = {styles.date}>
                    <Text>Date</Text>
                </View>

                <View style = {styles.time}>
                <Dropdown
                    style={[styles.dropdown, isFocus && {borderColor: 'blue'}]}
                    placeholderStyle={styles.placeholderStyle}
                    selectedTextStyle={styles.selectedTextStyle}
                    inputSearchStyle={styles.inputSearchStyle}
                    iconStyle={styles.iconStyle}
                    data={countryData}
                    search
                    maxHeight={300}
                    labelField="label"
                    valueField="value"
                    placeholder={!isFocus ? 'Select time' : '...'}
                    searchPlaceholder="Select time"
                    value={country}
                    onFocus={() => setIsFocus(true)}
                    onBlur={() => setIsFocus(false)}
                    onChange={item => {
                        setCountry(item.value);
                        handleState(item.value);
                        setCountryName(item.label);
                        setIsFocus(false);
                    }}
                    />
                </View>

                <View style = {styles.event}>
                    <Text>Event</Text>
                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        margin: 20,
        paddingHorizontal: 16,
    },
    input: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems:'center',
        marginBottom: 24, 
        borderBottomWidth: 1,
        borderBottomColor: 'grey'
    },
    textInput: {
        borderWidth: 1,
        borderColor: 'grey',
        width: '70%',
        marginRight: 8,
        padding: 8
    },
    goals: {
        flex: 5
    },
    goalItem: {
        margin: 8,
        borderRadius: 6,
        backgroundColor: '#5e0acc',
        padding: 8,
        color: 'white'
    },

    container: {
        flex: 1,
        backgroundColor: '#533483',
        padding: 16,
        justifyContent: 'center',
        alignContent: 'center',
    },
    dropdown: {
        height: 50,
        borderColor: 'gray',
        borderWidth: 0.5,
        borderRadius: 8,
        paddingHorizontal: 8,
        marginBottom: 10,
    },
    icon: {
        marginRight: 5,
    },
    label: {
        position: 'absolute',
        backgroundColor: 'white',
        left: 22,
        top: 8,
        zIndex: 999,
        paddingHorizontal: 8,
        fontSize: 14,
    },
    placeholderStyle: {
        fontSize: 16,
    },
    selectedTextStyle: {
        fontSize: 16,
    },
    iconStyle: {
        width: 20,
        height: 20,
    },
    inputSearchStyle: {
        height: 40,
        fontSize: 16,
    },


});
